update npc_text set text0_0='即使我也有我的局限性!如果你想繼續學習的話去找幽暗城的拉文尼亞·克洛文談談。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=5318;
