update npc_text set text0_0='你是在獵殺死亡彎鉤吧，$N?我記得很清楚。這週我手下的兩個傢夥就是被她殺死的。希望你的運氣比他們好。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=7751;
update npc_text set text0_0='嗨!謝謝你手下留情，沒有懲罰我!我想我得躺一會兒恢復體力。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=7752;
