update npc_text set text0_0='我是凱索，伊萊克的統治者，阿古斯之手的追隨者。你是新成員嗎?',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=9041;
