update npc_text set text0_0='我已經逃避我的命運夠久了。我的使命從未如此清楚。$B$B洛肯必須要被我親手毀滅。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13800;
update npc_text set text0_0='霍迪爾號角的外觀象徵了我們對安格林王的緬懷與其他在雷殞之地大戰中死去的人們。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13685;
