update npc_text set text0_0='哪有霜誕矮人，哪裡就有老鷹。他們從我在地上行走之前就是我們的盟友了…而且我也不年輕了，$G小子:小妞;。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13733;
update npc_text set text0_0='我不覺得我能辦得到，$G小夥子:小姑娘;。我失了太多的血…我沒辦法回去的。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13612;
update npc_text set text0_0='一群鷹身人從山谷裡出現。有一個叫做西拉納的大傢伙領著他們…我沒有地方可逃。他們很久都不這麼有侵略性了…我沒料到…我讓我的防衛鬆懈了…是我的錯。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13613;
update npc_text set text0_0='太…遲了…$G小夥子:小姑娘;。$B$B<霜誕哨兵在雪上咳出一些血。>$B$B去…幫我把那個蕩婦抓來。$B$B<霜誕哨兵斷了氣，頹然傾倒。>',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=13614;
