update npc_text set text0_0='我是赞达拉的赞札。我用以太形态从赞达拉赶来帮助我的人民。哈卡不能控制我的灵魂。$B$B我是来帮助那些曾经有恩於赞达拉的朋友的。',text0_1='',text1_0='',text1_1='',text2_0='',text2_1='',text3_0='',text3_1='',text4_0='',text4_1='',text5_0='',text5_1='',text6_0='',text6_1='',text7_0='',text7_1='' where id=7594;
