update script_texts set content_default='Ahhh... RAAAAAGH! Never thought... I would be free of him...'  where npc_entry=29120 and entry=-1601004;
update script_texts set content_default='Your armor is useless againts my locusts.'  where npc_entry=29120 and entry=-1601005;
update script_texts set content_default='Uunak-hissss tik-k-k-k-k!'  where npc_entry=29120 and entry=-1601006;
update script_texts set content_default='The pestilence upon you!'  where npc_entry=29120 and entry=-1601007;
