update script_texts set content_default='I was king of this empire once, long ago. In life I stood as champion. In death I returned as conqueror. Now I protect the kingdom once more. Ironic, yes? '  where npc_entry=29120 and entry=-1601010;
update script_texts set content_default='This kingdom belongs to the Scourge! Only the dead may enter.'  where npc_entry=28684 and entry=-1601011;
update script_texts set content_default='You were foolish to come.'  where npc_entry=28684 and entry=-1601012;
update script_texts set content_default='As Anub''Arak commands!'  where npc_entry=28684 and entry=-1601013;
update script_texts set content_default='I should be grateful. But I long ago lost the capacity.'  where npc_entry=28684 and entry=-1601014;
update script_texts set content_default='They hunger.'  where npc_entry=28684 and entry=-1601015;
update script_texts set content_default='Dinner time, my pets.'  where npc_entry=28684 and entry=-1601016;
update script_texts set content_default='Keep an eye on the tunnel. We must not let anyone through!'  where npc_entry=28684 and entry=-1601017;
update script_texts set content_default='I hear footsteps. Be on your guard.'  where npc_entry=28684 and entry=-1601018;
update script_texts set content_default='I sense the living. Be ready.'  where npc_entry=28684 and entry=-1601019;
update script_texts set content_default='We are besieged. Strike out and bring back their corpses!'  where npc_entry=28684 and entry=-1601020;
update script_texts set content_default='We must hold the gate. Attack! Tear them limb from limb!'  where npc_entry=28684 and entry=-1601021;
update script_texts set content_default='The gate must be protected at all costs. Rip them to shreds!'  where npc_entry=28684 and entry=-1601022;
update script_texts set content_default='你阻止不了赛尔赞的!他拥着黑暗礼赞，由巫妖王亲自赐予他! '  where npc_entry=27577 and entry=-1800038;
update script_texts set content_default='怎么杀?人类真的很笨，对吧?『幽暮使者』赛尔赞!『巫妖』赛尔赞!他以人类的骨血而生，却为了力量牺牲奉献了一切，受到巫妖王的庇护! '  where npc_entry=27577 and entry=-1800037;
update script_texts set content_default='人类…你们在温特加德村之下创建了一个墓冢!你以为为什么纳克萨玛斯要攻击这个地方?赛尔赞躲在你们墓穴的深处，派出成千上万的尸体攻击你!你等死吧…… '  where npc_entry=27577 and entry=-1800035;
update script_texts set content_default='不不不!我告诉你!我什么都说! '  where npc_entry=27577 and entry=-1800034;
update script_texts set content_default='它说这即将到来的启示。这个世界将如何毁灭，然后再从死亡中重生。 '  where npc_entry=27577 and entry=-1800032;
update script_texts set content_default='发出嚎叫。 '  where npc_entry=27577 and entry=-1800028;
update script_texts set content_default='嗤之以鼻! '  where npc_entry=27577 and entry=-1800025;
update script_texts set content_default='那本书是你的救赎，没错…但你什么也不知道!我什么也不说!绝不! '  where npc_entry=27577 and entry=-1800020;
update script_texts set content_default='你能对我做什么是科尔苏加德做不出来的?或是巫妖王也做不到的? '  where npc_entry=27577 and entry=-1800019;
update script_texts set content_default='什么也不会告诉你，传教士。'  where npc_entry=27577 and entry=-1800018;
update script_texts set content_default='谢谢你. $C女孩!'  where npc_entry=27463 and entry=-1800046;
update script_texts set content_default='吓啊 - 我差点死在这，谢谢你， $Race!'  where npc_entry=27463 and entry=-1800045;
update script_texts set content_default='啊…好一点了。'  where npc_entry=27463 and entry=-1800044;
update script_texts set content_default='准备好跟我说一声。快一点比慢一点好…像是慢慢地中毒死去。'  where npc_entry=29434 and entry=-1800042;
update script_texts set content_default='我准备带着毒囊给瑞基特…接着…你知道的…毁灭。谢谢你帮助我!'  where npc_entry=29434 and entry=-1800043;
update script_texts set content_default='快点离开这个鬼地方。 '  where npc_entry=28787 and entry=-1800047;
update script_texts set content_default='听着，风险投资公司的呆子!第1条规则:不要把囚犯放在靠近炸药的地方。 '  where npc_entry=28787 and entry=-1800048;
update script_texts set content_default='否则，『这』就是你会得到的。 '  where npc_entry=28787 and entry=-1800049;
update script_texts set content_default='这里变得有点热了。我们继续前进吧? '  where npc_entry=28787 and entry=-1800050;
update script_texts set content_default='喔，看，又另一大车的炸药!让我们来帮忙处理它。 '  where npc_entry=28787 and entry=-1800051;
update script_texts set content_default='你真的不应该玩这些东西。有人可能会受伤。 '  where npc_entry=28787 and entry=-1800052;
update script_texts set content_default='我们办到了!感谢你帮我逃出那个该死的洞穴。告诉赫米特要等着我! '  where npc_entry=28787 and entry=-1800053;
update script_texts set content_default='Yggdras emerges!'  where npc_entry=30014 and entry=-1571039;
update script_texts set content_default='Stinkbeard comin'' for you, little ones!'  where npc_entry=30017 and entry=-1571040;
update script_texts set content_default='Do you fell that folks? The air is cracking with energy! Than can only mean one thing...'  where npc_entry=30007 and entry=-1571041;
update script_texts set content_default='这个地方的冰冷…我感到血液在凝结。'  where npc_entry=37221 and entry=-1668001;
update script_texts set content_default='那是什么!前面那个!这可能吗?英雄们，到我旁边来!'  where npc_entry=37221 and entry=-1668002;
update script_texts set content_default='霜之哀伤:摧毁我们王国的那把剑……'  where npc_entry=37221 and entry=-1668003;
update script_texts set content_default='退后!触碰那把剑将会永远毁伤你的灵魂!我一定要试着跟那些被困在霜之哀伤中的灵魂沟通。给我一点空间。请退后。'  where npc_entry=37221 and entry=-1668004;
update script_texts set content_default='Jaina! Could it truly be you?'  where npc_entry=37225 and entry=-1668005;
update script_texts set content_default='乌瑟!亲爱的乌瑟!我…我真的很抱歉。'  where npc_entry=37221 and entry=-1668006;
update script_texts set content_default='珍娜，你们没有太多时间。巫妖王可以看到剑所看到的一切。他很快就会出现。'  where npc_entry=37225 and entry=-1668007;
update script_texts set content_default='阿萨斯在这?或许我……'  where npc_entry=37221 and entry=-1668008;
update script_texts set content_default='不，女孩。阿萨斯不在这里。阿萨斯只存在于巫妖王的心中。一个逐渐消退的存在……'  where npc_entry=37225 and entry=-1668009;
update script_texts set content_default='但乌瑟，如果有任何机会能靠近阿萨斯。我…我一定要试试。'  where npc_entry=37221 and entry=-1668010;
update script_texts set content_default='珍娜，听我说。你一定要摧毁巫妖王。你不可能跟他讲道理。他会杀死你和你的盟友，然后把你们全都变成天谴军团的强大士兵。'  where npc_entry=37225 and entry=-1668011;
update script_texts set content_default='告诉我该怎么做，乌瑟。我要怎么毁灭我的王子?我的……'  where npc_entry=37221 and entry=-1668012;
update script_texts set content_default='振作起来，女孩。你一定要在巫妖王和耐祖奥融合之处摧毁他 - 就在尖塔之顶，就在冰封王座。这是唯一的方法。'  where npc_entry=37225 and entry=-1668013;
update script_texts set content_default='你是对的，乌瑟。原谅我。我…我不知道是什么让我这样。我们会把消息传给国王与那些在冰冠城塞对抗天谴军的骑士。'  where npc_entry=37221 and entry=-1668014;
update script_texts set content_default='有…有件关于巫妖王的事情你需要知道。天谴军团绝对不能失去控制。就算你击倒巫妖王，另一个人就得取代他的位置。要是没有巫妖王的控制，天谴军团将会像蝗虫般横扫整个世界 - 摧毁所有的生灵。'  where npc_entry=37225 and entry=-1668015;
update script_texts set content_default='一个高贵灵魂的伟大牺牲……'  where npc_entry=37225 and entry=-1668016;
update script_texts set content_default='谁能肩负如此重担?'  where npc_entry=37221 and entry=-1668017;
update script_texts set content_default='我不知道，珍娜。我怀疑阿萨斯的一部份仍然存在于巫妖王之中，这也是为什么天谴军团一直没有灭绝艾泽拉斯。'  where npc_entry=37225 and entry=-1668018;
update script_texts set content_default='Then maybe there is still hope...'  where npc_entry=37221 and entry=-1668019;
update script_texts set content_default='不，珍娜!啊啊啊啊啊啊…他…他来了。你…你们一定……'  where npc_entry=37225 and entry=-1668020;
update script_texts set content_default='...我不相信！霜之哀伤，毫无防备！正如侏儒所说的。来吧，英雄！'  where npc_entry=37223 and entry=-1668021;
update script_texts set content_default='就是这把剑终结了我的生命... 这种痛苦... 挥之不去！'  where npc_entry=37223 and entry=-1668022;
update script_texts set content_default='我不敢去碰它。退后！退后...'  where npc_entry=37223 and entry=-1668023;
update script_texts set content_default='小心！女孩。'  where npc_entry=37225 and entry=-1668024;
update script_texts set content_default='乌瑟尔！光明使者乌瑟尔...。怎样...'  where npc_entry=37223 and entry=-1668025;
update script_texts set content_default='你没有时间了. 巫妖王能看到剑旁的一切.他很快就会来到这里.'  where npc_entry=37225 and entry=-1668026;
update script_texts set content_default='巫妖王在这里？那么今天我的愿望就会实现！'  where npc_entry=37223 and entry=-1668027;
update script_texts set content_default='珍娜，听我说。你一定要摧毁巫妖王。你不可能跟他讲道理。他会杀死你和你的盟友，然后把你们全都变成天谴军团的强大士兵。'  where npc_entry=37225 and entry=-1668028;
update script_texts set content_default='必须有一种方式...'  where npc_entry=37223 and entry=-1668029;
update script_texts set content_default='有…有件关于巫妖王的事情你需要知道。天谴军团绝对不能失去控制。就算你击倒巫妖王，另一个人就得取代他的位置。要是没有巫妖王的控制，天谴军团将会像蝗虫般横扫整个世界 - 摧毁所有的生灵。'  where npc_entry=37225 and entry=-1668030;
update script_texts set content_default='谁能承受？'  where npc_entry=37223 and entry=-1668031;
update script_texts set content_default='我不知道，珍娜。我怀疑阿萨斯的一部份仍然存在于巫妖王之中，这也是为什么天谴军团一直没有灭绝艾泽拉斯。'  where npc_entry=37225 and entry=-1668032;
update script_texts set content_default='唉，只有这样，才能击败巫妖王...'  where npc_entry=37225 and entry=-1668033;
update script_texts set content_default='冰封王座...'  where npc_entry=37223 and entry=-1668034;
update script_texts set content_default='啊！...他...他来了...你...你必须...'  where npc_entry=37225 and entry=-1668035;
update script_texts set content_default='闭嘴，圣骑士！'  where npc_entry=37226 and entry=-1668036;
update script_texts set content_default='你这么喜欢和私人交谈? 我会满足你的.'  where npc_entry=37226 and entry=-1668037;
update script_texts set content_default='法勒瑞克。麦尔温。杀掉他们。带到我的大殿来。'  where npc_entry=37226 and entry=-1668038;
update script_texts set content_default='如您所愿，大人。 '  where npc_entry=38112 and entry=-1668039;
update script_texts set content_default='如您所愿，大人。 '  where npc_entry=38113 and entry=-1668040;
update script_texts set content_default='罗德隆的士兵们，起来响应你们主人的召唤! '  where npc_entry=38112 and entry=-1668041;
update script_texts set content_default='你不能否认我说的，阿萨斯!我一定要知道…我一定要找出来……'  where npc_entry=37221 and entry=-1668042;
update script_texts set content_default='你不会轻松逃脱，阿尔萨斯！'  where npc_entry=37223 and entry=-1668043;
update script_texts set content_default='男人、女人和小孩…没有人能逃过主人的愤怒。你的死也没有差别。 '  where npc_entry=38112 and entry=-1668050;
update script_texts set content_default='流鼻涕的蛆! '  where npc_entry=38112 and entry=-1668051;
update script_texts set content_default='斯坦索姆的孩童都比你会战斗! '  where npc_entry=38112 and entry=-1668052;
update script_texts set content_default='麦尔温，解决他们…… '  where npc_entry=38112 and entry=-1668053;
update script_texts set content_default='绝望…多么美味…… '  where npc_entry=38112 and entry=-1668054;
update script_texts set content_default='恐惧…多令人振奋…… '  where npc_entry=38112 and entry=-1668055;
update script_texts set content_default='你们在这里只能找到死亡! '  where npc_entry=38113 and entry=-1668060;
update script_texts set content_default='他死的时候，我在他眼中看到同样的眼神。泰瑞纳斯几乎不能相信。 '  where npc_entry=38113 and entry=-1668061;
update script_texts set content_default='被你的痛苦噎死吧! '  where npc_entry=38113 and entry=-1668062;
update script_texts set content_default='是…跑吧…跑去面对你的命运…它苦涩、冰冷的拥抱正等着你。 '  where npc_entry=38113 and entry=-1668063;
update script_texts set content_default='你的血肉会在你眼前腐烂! '  where npc_entry=38113 and entry=-1668064;
update script_texts set content_default='消失殆尽吧! '  where npc_entry=38113 and entry=-1668065;
update script_texts set content_default='What do we have here... those that would defy the Spell-Weaver? Those without foresight our understanding. How can i make you see? Malygos is saving the world from itself! Bah! You are hardly worth my time!'  where npc_entry=27655 and entry=-1578000;
update script_texts set content_default='Clearly my pets failed. Perhaps another demonstration is in order.'  where npc_entry=27655 and entry=-1578001;
update script_texts set content_default='Still you fight. Still you cling to misguided principles. If you survive, you''ll find me in the center ring.'  where npc_entry=27655 and entry=-1578002;
update script_texts set content_default='Poor blind fools!'  where npc_entry=27655 and entry=-1578003;
update script_texts set content_default='A taste... just a small taste... of the Spell-Weaver''s power!'  where npc_entry=27655 and entry=-1578004;
update script_texts set content_default='You invade my home and then dare to challenge me? I will tear the hearts from your chests and offer them as gifts to the death god! Rualg nja gaborr!'  where npc_entry=26861 and entry=-1575028;
update script_texts set content_default='Your death is only the beginning!'  where npc_entry=26861 and entry=-1575029;
update script_texts set content_default='You have failed your people!'  where npc_entry=26861 and entry=-1575030;
update script_texts set content_default='There is a reason I am king!'  where npc_entry=26861 and entry=-1575031;
update script_texts set content_default='Bleed no more!'  where npc_entry=26861 and entry=-1575032;
update script_texts set content_default='What... awaits me... now?'  where npc_entry=26861 and entry=-1575033;
update script_texts set content_default='Bjorn of the Black Storm! Honor me now with your presence!'  where npc_entry=26861 and entry=-1575034;
update script_texts set content_default='Haldor of the Rocky Cliffs, grant me your strength!'  where npc_entry=26861 and entry=-1575035;
update script_texts set content_default='Ranulf of the Screaming Abyss, snuff these maggots with darkest night! '  where npc_entry=26861 and entry=-1575036;
update script_texts set content_default='Tor of the Brutal Siege! Bestow your might upon me!'  where npc_entry=26861 and entry=-1575037;
update script_texts set content_default='什么样的狗杂种竟然胆敢入侵这里？快点，弟兄们！谁要是能把他们的头提来，就赏他吃肉！'  where npc_entry=26693 and entry=-1575004;
update script_texts set content_default='你现在怎么不那么嚣张了啊？'  where npc_entry=26693 and entry=-1575005;
update script_texts set content_default='我要把你的脑袋插到最高的塔尖上去！'  where npc_entry=26693 and entry=-1575006;
update script_texts set content_default='%s 在鱼叉的射程内!'  where npc_entry=26693 and entry=-1575007;
update script_texts set content_default='啊啊！这也能叫……攻击？我要……让你们……见识见识……啊啊啊……'  where npc_entry=26693 and entry=-1575008;
update script_texts set content_default='%s 在鱼叉的射程内!'  where npc_entry=26693 and entry=-1575009;
update script_texts set content_default='你这只无能的蠢龙！你的尸体干脆给我的新飞龙拿去当点心算了！'  where npc_entry=26693 and entry=-1575010;
update script_texts set content_default='把他们都烧成焦炭!'  where npc_entry=26693 and entry=-1575011;
update script_texts set content_default='上啊,除了灰烬什么也不要留下!'  where npc_entry=26693 and entry=-1575012;
